import{_ as a,c as s,o as i,ac as t}from"./chunks/framework.BZbTqp8-.js";const g=JSON.parse('{"title":"Developing on GNU/Linux","description":"","frontmatter":{},"headers":[],"relativePath":"en/Develop_on_Linux.md","filePath":"en/Develop_on_Linux.md","lastUpdated":1760594121000}'),n={name:"en/Develop_on_Linux.md"};function l(o,e,p,d,h,r){return i(),s("div",null,[...e[0]||(e[0]=[t('<h1 id="developing-on-gnu-linux" tabindex="-1">Developing on GNU/Linux <a class="header-anchor" href="#developing-on-gnu-linux" aria-label="Permalink to “Developing on GNU/Linux”">​</a></h1><h2 id="step-1-install-xmake-and-qt-6" tabindex="-1">Step 1: Install xmake and Qt 6 <a class="header-anchor" href="#step-1-install-xmake-and-qt-6" aria-label="Permalink to “Step 1: Install xmake and Qt 6”">​</a></h2><p>On Debian or Debian derivatives:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>sudo apt install xmake</span></span>\n<span class="line"><span></span></span>\n<span class="line"><span>sudo apt update</span></span>\n<span class="line"><span>sudo apt install --yes build-essential libfontconfig1-dev  qt6-base-dev libqt6svg6-dev qt6-image-formats-plugins libcurl4-openssl-dev libfreetype-dev libgit2-dev zlib1g-dev libssl-dev libjpeg-turbo8-dev cmake</span></span></code></pre></div><p>Sometimes, we need the latest xrepo:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>xrepo update-repo</span></span></code></pre></div><h2 id="step-2-build" tabindex="-1">Step 2: Build <a class="header-anchor" href="#step-2-build" aria-label="Permalink to “Step 2: Build”">​</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>xmake config --yes</span></span>\n<span class="line"><span>xmake build stem</span></span></code></pre></div><p>If Qt SDK is not found, we can config it manually:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>xmake config --qt=/usr/lib/`arch`-linux-gnu/qt6/</span></span></code></pre></div><p>To switch to Qt 5, just:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>xmake config --qt=/usr/lib/`arch`-linux-gnu/qt5/</span></span></code></pre></div><h2 id="step-3-run-tests" tabindex="-1">Step 3: Run tests <a class="header-anchor" href="#step-3-run-tests" aria-label="Permalink to “Step 3: Run tests”">​</a></h2><p>See <a href="./Test.html">How to test</a>.</p><h2 id="step-4-launch-mogan-stem" tabindex="-1">Step 4: Launch Mogan STEM <a class="header-anchor" href="#step-4-launch-mogan-stem" aria-label="Permalink to “Step 4: Launch Mogan STEM”">​</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">xmake</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> run</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> stem</span></span></code></pre></div><h2 id="use-vscode-to-help-code-completion" tabindex="-1">Use VSCode to help code completion <a class="header-anchor" href="#use-vscode-to-help-code-completion" aria-label="Permalink to “Use VSCode to help code completion”">​</a></h2><p>Install VSCode, Clangd and the Clangd plugin for VSCode. Then execute in the mogan folder</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>xmake project -k compile_commands</span></span></code></pre></div><p>This will generate a compile_commands.json file under the mogan folder, and Clangd will read it to understand the organizational structure of the project, so as to avoid the error that the header file cannot be found.</p>',20)])])}const k=a(n,[["render",l]]);export{g as __pageData,k as default};
