import{_ as a,c as t,o as r,aa as i}from"./chunks/framework.BuJSL0aO.js";const u=JSON.parse('{"title":"Project: Mogan Draw on WASM","description":"","frontmatter":{},"headers":[],"relativePath":"en/OSPP_2023_Mogan_Draw.md","filePath":"en/OSPP_2023_Mogan_Draw.md","lastUpdated":1760514920000}'),o={name:"en/OSPP_2023_Mogan_Draw.md"};function n(s,e,l,c,h,m){return r(),t("div",null,[...e[0]||(e[0]=[i('<h1 id="project-mogan-draw-on-wasm" tabindex="-1">Project: Mogan Draw on WASM <a class="header-anchor" href="#project-mogan-draw-on-wasm" aria-label="Permalink to “Project: Mogan Draw on WASM”">​</a></h1><ul><li>OSPP 2023 Page: <a href="https://summer-ospp.ac.cn/2023/org/prodetail/23b740366?lang=zh&amp;list=pro" target="_blank" rel="">https://summer-ospp.ac.cn/2023/org/prodetail/23b740366?lang=zh&amp;list=pro</a></li><li>Forum: <a href="http://forum.texmacs.cn/t/project-mogan-draw-on-wasm/1424" target="_blank" rel="">http://forum.texmacs.cn/t/project-mogan-draw-on-wasm/1424</a></li><li>Tech Tag: Wasm, C++</li></ul><h2 id="description" tabindex="-1">Description <a class="header-anchor" href="#description" aria-label="Permalink to “Description”">​</a></h2><p>Improve the built-in tool for making technical graphics and make it user-friendly enough for most users.</p><p>Imagine that you are a teacher in a junior high school teaching Euclid’s Elements, and you are using Mogan Editor/GNU TeXmacs to prepare the slides.</p><p>Here is the link to Euclid’s Element (in Greek), you should be able to re-draw the figures in Euclid’s Element via Mogan Draw in an easy way (straight-forward and no need to remember any tricks) and can persuade your colleagues to use Mogan Draw.</p><p><a href="http://www.physics.ntua.gr/~mourmouras/euclid/" target="_blank" rel="">http://www.physics.ntua.gr/~mourmouras/euclid/</a></p><h2 id="output-requirements" tabindex="-1">Output Requirements <a class="header-anchor" href="#output-requirements" aria-label="Permalink to “Output Requirements”">​</a></h2><ul><li>User Interface Re-design: make it easy-to-use and straight-forward</li><li>A report or design doc of the new interface and its comparison between Quiver and Mathcha</li><li>New features and bug fixes for math students and teachers <ul><li>feature: Draw a circle with its center and radius and more basic commonly used figures</li><li>feature: Implement various kinds of arrows and decorations</li><li>bug fix: Fix severe bugs in the current implementation</li></ul></li><li>Mogan Draw: the wasm app <ul><li>host it on <a href="https://draw.mogan.app" target="_blank" rel="">https://draw.mogan.app</a> with the help of the community</li><li>make it as smaller as possible</li><li>make it a standalone app for drawing figures</li></ul></li><li>re-draw some figures in Publicity: Euclid’s Element via Mogan Draw and create videos to show the detailed steps to draw the figures and then upload it to Bilibili or YouTube</li></ul><h2 id="technical-requirements" tabindex="-1">Technical Requirements <a class="header-anchor" href="#technical-requirements" aria-label="Permalink to “Technical Requirements”">​</a></h2><ul><li>xmake, C++, Scheme</li><li>Understanding the data model of the Graphics tool</li><li>Understanding the layout engine</li><li>Basic Knowledge Computer Graphics</li></ul><h2 id="project-repository" tabindex="-1">Project Repository <a class="header-anchor" href="#project-repository" aria-label="Permalink to “Project Repository”">​</a></h2><p>Choose one of them:</p><ul><li><a href="https://codeberg.org/XmacsLabs/mogan" target="_blank" rel="">https://codeberg.org/XmacsLabs/mogan</a></li><li><a href="https://github.com/XmacsLabs/mogan" target="_blank" rel="">https://github.com/XmacsLabs/mogan</a></li><li><a href="https://gitee.com/XmacsLabs/mogan" target="_blank" rel="">https://gitee.com/XmacsLabs/mogan</a></li></ul>',14)])])}const p=a(o,[["render",n]]);export{u as __pageData,p as default};
