import{_ as o,c as a,a2 as t,o as i}from"./chunks/framework.YPpNXepj.js";const m=JSON.parse('{"title":"Mogan STEM Suite v1.2.3","description":"","frontmatter":{},"headers":[],"relativePath":"guide/ChangeLog_v1.2.3.md","filePath":"guide/ChangeLog_v1.2.3.md","lastUpdated":1736323506000}'),l={name:"guide/ChangeLog_v1.2.3.md"};function n(r,e,s,d,c,h){return i(),a("div",null,e[0]||(e[0]=[t('<h1 id="mogan-stem-suite-v1-2-3" tabindex="-1">Mogan STEM Suite v1.2.3 <a class="header-anchor" href="#mogan-stem-suite-v1-2-3" aria-label="Permalink to &quot;Mogan STEM Suite v1.2.3&quot;">​</a></h1><p>Mogan STEM Suite v1.2.3:</p><ul><li>Mogan Research v1.2.3</li></ul><h2 id="important-changes" tabindex="-1">Important Changes <a class="header-anchor" href="#important-changes" aria-label="Permalink to &quot;Important Changes&quot;">​</a></h2><ul><li>Performance improvements</li><li>Many bug fixes</li><li>User Interface improvements</li></ul><h2 id="detailed-changes-that-affect-users" tabindex="-1">Detailed changes that affect users <a class="header-anchor" href="#detailed-changes-that-affect-users" aria-label="Permalink to &quot;Detailed changes that affect users&quot;">​</a></h2><ul><li>User Interface <ul><li>Make <code>Tools-&gt;Keyboard-&gt;Show key presses</code> work on Windows and Linux</li><li>Fix: crashes at opening on macOS arm with external monitor</li><li>Fix: no priviledge to access <code>$HOME/Document</code> on macOS</li></ul></li><li>Editor <ul><li>Add <code>std V</code>（<code>Ctrl+Shift+v</code> or <code>Command+Shift+v</code>）to paste verbatim</li><li>Add the menu entry <code>Edit-&gt;Paste verbatim</code></li><li>Remove <code>Primary/Seconday/Ternary</code> in Edit-&gt;<code>Copy/Paste/Cut</code></li><li>macOS: use <code>Command+left</code> or <code>Command+right</code> to move by word</li><li>Insert hlink (<code>[ ] var</code>) or slink (<code>[ ] var var</code>) using markdown style shortcuts</li><li>Fix input method lost when the cursor is at the start of the line especially for old computers</li><li>Fix failure to zoom out by <code>Command+minus</code> on macOS</li></ul></li><li>Font <ul><li>Improve support on the Noto CJK fonts</li><li>Set default CJK fonts to Noto CJK on all platform if exists</li><li>Support CESI fonts</li></ul></li><li>Plugins <ul><li>Maxima: Fixed the slowness in the editing area</li><li>Maxima: visit the remote online Maxima manual when click the Help icon on the focus toolbar</li><li>Spell: enable <code>Edit-&gt;Spell</code> on Windows and Linux</li><li>Spell: Fixed the crash on Windows when neither <code>hunspell</code> nor <code>aspell</code> is installed</li><li>LaTeX: Fixed the incorrect encoding when copying the several equations to LaTeX snippet</li></ul></li></ul><h2 id="changes-for-developer" tabindex="-1">Changes for Developer <a class="header-anchor" href="#changes-for-developer" aria-label="Permalink to &quot;Changes for Developer&quot;">​</a></h2><ul><li>Use <code>xmake-requires.lock</code> to make the build system more reproducible</li><li>the installer on macOS arm is now packaged by github action</li></ul>',9)]))}const p=o(l,[["render",n]]);export{m as __pageData,p as default};
