import{_ as e,c as i,o as l,a4 as t}from"./chunks/framework.B2EPA2lv.js";const f=JSON.parse('{"title":"Mogan STEM Suite v1.2.5 LTS","description":"","frontmatter":{},"headers":[],"relativePath":"guide/ChangeLog_v1.2.5.md","filePath":"guide/ChangeLog_v1.2.5.md","lastUpdated":1730339162000}'),o={name:"guide/ChangeLog_v1.2.5.md"},a=t('<h1 id="mogan-stem-suite-v1-2-5-lts" tabindex="-1">Mogan STEM Suite v1.2.5 LTS <a class="header-anchor" href="#mogan-stem-suite-v1-2-5-lts" aria-label="Permalink to &quot;Mogan STEM Suite v1.2.5 LTS&quot;">​</a></h1><p>Mogan STEM Suite v1.2.5 LTS:</p><ul><li>Mogan Research v1.2.5 LTS</li></ul><p>This release is for bug fixes and user experience improvements.</p><h2 id="notable-changes-in-v1-2-5-4-2024-05-04" tabindex="-1">Notable Changes in v1.2.5.4 (2024/05/04) <a class="header-anchor" href="#notable-changes-in-v1-2-5-4-2024-05-04" aria-label="Permalink to &quot;Notable Changes in v1.2.5.4 (2024/05/04)&quot;">​</a></h2><ul><li>Graphics mode <ul><li>Fix copy/cut of the whole graphics area when it was selected via mouse</li></ul></li><li>User Interface <ul><li>Add <code>View-&gt;Focus mode</code> as a replacement of <code>View-&gt;Header bars</code></li></ul></li></ul><h2 id="notable-changes-in-v1-2-5-3-2024-04-15" tabindex="-1">Notable Changes in v1.2.5.3 (2024/04/15) <a class="header-anchor" href="#notable-changes-in-v1-2-5-3-2024-04-15" aria-label="Permalink to &quot;Notable Changes in v1.2.5.3 (2024/04/15)&quot;">​</a></h2><ul><li>Plugins <ul><li>The Python plugin now supports using virtual environments created by conda</li></ul></li><li>Bug Fixes <ul><li>Fixed the issue that using a separate window for structured replacement may cause Mogan to crash</li></ul></li></ul><h2 id="notable-changes-in-v1-2-5-2-2024-04-08" tabindex="-1">Notable Changes in v1.2.5.2 (2024/04/08) <a class="header-anchor" href="#notable-changes-in-v1-2-5-2-2024-04-08" aria-label="Permalink to &quot;Notable Changes in v1.2.5.2 (2024/04/08)&quot;">​</a></h2><ul><li>Bug fixes <ul><li>Fixed the issue where single replacement and replace all functions in the bottom toolbar do not work when clicked with a mouse</li><li>Fixed the issue where the &quot;File -&gt; Export -&gt; Editable PDF&quot; function does not work when the PDF version is greater than 1.4</li><li>Fixed the layout engine error caused by executing <code>ones (0)</code> in the Octave plugin</li><li>Fixed the issue where the Portuguese dictionary file is ineffective due to incorrect naming</li><li>Fixed the issue where styles and images in <code>$HOME/Documents</code> cannot be referenced on the macOS platform</li><li>Set <code>qt6-qpa-plugins</code> as an explicit dependency to solve the issue of not being able to start normally after installation on Ubuntu</li></ul></li><li>Slide Styles <ul><li>Adjusted the default page type to <code>16:9</code></li><li>Defaulted slide presentation to fit width</li><li>When the slides are rendered as book, the document will be automatically adjusted to fit the width upon opening.</li></ul></li><li>Plugins <ul><li>Unified the calls to executable files into binary plugins</li><li>Built-in Python plugin, where the location of the Python interpreter is determined by the Python binary plugin</li></ul></li></ul><h2 id="notable-changes-in-v1-2-5-1-2024-03-14" tabindex="-1">Notable changes in v1.2.5.1 (2024/03/14) <a class="header-anchor" href="#notable-changes-in-v1-2-5-1-2024-03-14" aria-label="Permalink to &quot;Notable changes in v1.2.5.1 (2024/03/14)&quot;">​</a></h2><ul><li>Shortcuts <ul><li>Fix for the unexpected commit of the ESC key</li><li>Fix the keyboard insert of backtick (For the SICP Open Course)</li></ul></li><li>User Interface <ul><li>Fix messy UI style of <code>View-&gt;Full screen mode</code></li></ul></li><li>Beamer mode <ul><li>Add the slidemove plugin authored by <a href="https://github.com/woutersj" target="_blank" rel="noreferrer">@woutersj</a> to adjust the orders of the slides</li></ul></li><li>Scheme session <ul><li>Fix encoding of <code>scheme-eval</code> (For the SICP Open Course)</li></ul></li></ul><h2 id="notable-changes-in-v1-2-5-2024-02-27" tabindex="-1">Notable changes in v1.2.5 (2024/02/27) <a class="header-anchor" href="#notable-changes-in-v1-2-5-2024-02-27" aria-label="Permalink to &quot;Notable changes in v1.2.5 (2024/02/27)&quot;">​</a></h2><ul><li>Installers <ul><li>Official deb installer for Ubuntu 20.04</li><li>Official deb installer for Debian 12 (bookworm)</li><li>Portable installer for Windows 10/11</li></ul></li><li>Beamer mode <ul><li>Fix failure to change the background image when changing the theme on macOS</li></ul></li><li>Shortcuts <ul><li>macOS: Command +/- with/without shift to zoom in/out</li><li>Fix unexpected commit of unrecognized shortcuts</li></ul></li><li>User Interface <ul><li>Fix open file via double click with unicode/CJK name on macOS</li></ul></li><li>Layout Engine <ul><li>Fix the region invalidation issue when deleting</li><li>Fix failure when setting the columns number of the specific content to 2 in beamer mode</li><li>Support numbers in Chinese for the number primitive</li></ul></li><li>Performance Improvements <ul><li>Improve the speed of parsing TeXmacs documents</li><li>Improve the speed of rendering Pine beamer theme</li></ul></li><li>PDF Import <ul><li>Add <code>File-&gt;Import-&gt;PDF with embedded document...</code> instead of <code>File-&gt;Open</code> the &quot;editable&quot; PDF document</li></ul></li><li>Version Control <ul><li>Fix the viewer of the history version in Version-&gt;History on Windows</li></ul></li><li>Image plugins <ul><li>Improve the support for Postscript image format</li><li>Improve the support for SVG image format</li></ul></li><li>Session plugin <ul><li>Fix the encoding of CJK characters before and after scheme-eval</li></ul></li><li>Code plugin <ul><li>Re-add the syntax highlighter for Python</li></ul></li></ul>',14),n=[a];function s(r,u,h,c,d,g){return l(),i("div",null,n)}const p=e(o,[["render",s]]);export{f as __pageData,p as default};
