import{_ as i,c as e,o as t,a3 as n}from"./chunks/framework.D5f1jjZh.js";const u=JSON.parse('{"title":"Maxima Session Plugin","description":"","frontmatter":{},"headers":[],"relativePath":"guide/plugin_maxima.md","filePath":"guide/plugin_maxima.md","lastUpdated":null}'),s={name:"guide/plugin_maxima.md"};function l(o,a,r,d,h,c){return t(),e("div",null,a[0]||(a[0]=[n('<h1 id="maxima-session-plugin" tabindex="-1">Maxima Session Plugin <a class="header-anchor" href="#maxima-session-plugin" aria-label="Permalink to &quot;Maxima Session Plugin&quot;">​</a></h1><p>Video Tutorial in Chinese: <a href="https://www.bilibili.com/video/BV1JJ4m1V7Mq/" target="_blank" rel="">零基础墨干09：用Maxima插件学微积分</a></p><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><p>The Maxima session plugin relies on the <a href="./plugin_binary_gs.html">Ghostscript Binary plugin</a>. Please follow the documentation to install Ghostscript first, and then check <code>Help -&gt; Plugins -&gt; Maxima</code> to ensure that the PDF images in the documentation can be rendered correctly.</p><h3 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h3><p>Download and install from the <a href="https://maxima.sourceforge.io" target="_blank" rel="">Maxima official website</a>.</p><p>The recommended version of Maxima is 5.47.0.</p><h3 id="macos" tabindex="-1">macOS <a class="header-anchor" href="#macos" aria-label="Permalink to &quot;macOS&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>brew install maxima</span></span></code></pre></div><h3 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h3><p>On Debian and its derivatives, use the following command to install:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo apt install maxima</span></span></code></pre></div><h2 id="tips" tabindex="-1">Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;Tips&quot;">​</a></h2><ul><li>Ubuntu: Maxima installed using snap cannot work with Mogan. <ul><li>This is due to the mechanism of snap, so it is not recommended to install Maxima using snap.</li></ul></li></ul><h2 id="check-built-in-doc-at-help-plugins-maxima" tabindex="-1">Check built-in Doc at <code>Help -&gt; Plugins -&gt; Maxima</code> <a class="header-anchor" href="#check-built-in-doc-at-help-plugins-maxima" aria-label="Permalink to &quot;Check built-in Doc at `Help -&gt; Plugins -&gt; Maxima`&quot;">​</a></h2>',15)]))}const m=i(s,[["render",l]]);export{u as __pageData,m as default};
