import{_ as i,c as e,o as s,aa as n}from"./chunks/framework.B4EQu3MT.js";const p=JSON.parse('{"title":"Maxima Session Plugin","description":"","frontmatter":{},"headers":[],"relativePath":"guide/plugin_maxima.md","filePath":"guide/plugin_maxima.md","lastUpdated":1755437191000}'),t={name:"guide/plugin_maxima.md"};function l(o,a,r,h,d,c){return s(),e("div",null,[...a[0]||(a[0]=[n('<h1 id="maxima-session-plugin" tabindex="-1">Maxima Session Plugin <a class="header-anchor" href="#maxima-session-plugin" aria-label="Permalink to “Maxima Session Plugin”">​</a></h1><p>Video Tutorial in Chinese: <a href="https://www.bilibili.com/video/BV1JJ4m1V7Mq/" target="_blank" rel="">零基础墨干09：用Maxima插件学微积分</a></p><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to “Installation”">​</a></h2><p>The Maxima session plugin relies on the <a href="./plugin_binary_gs.html">Ghostscript Binary plugin</a>. Please follow the documentation to install Ghostscript first, and then check <code>Help -&gt; Plugins -&gt; Maxima</code> to ensure that the PDF images in the documentation can be rendered correctly.</p><h3 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to “Windows”">​</a></h3><p>Download and install from the <a href="https://maxima.sourceforge.io" target="_blank" rel="">Maxima official website</a>.</p><p>The recommended version of Maxima is 5.47.0.</p><h3 id="macos" tabindex="-1">macOS <a class="header-anchor" href="#macos" aria-label="Permalink to “macOS”">​</a></h3><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>brew install maxima</span></span></code></pre></div><h3 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to “Linux”">​</a></h3><p>On Debian and its derivatives, use the following command to install:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>sudo apt install maxima</span></span></code></pre></div><h2 id="tips" tabindex="-1">Tips <a class="header-anchor" href="#tips" aria-label="Permalink to “Tips”">​</a></h2><ul><li>Ubuntu: Maxima installed using snap cannot work with Mogan. <ul><li>This is due to the mechanism of snap, so it is not recommended to install Maxima using snap.</li></ul></li></ul><h2 id="check-built-in-doc-at-help-plugins-maxima" tabindex="-1">Check built-in Doc at <code>Help -&gt; Plugins -&gt; Maxima</code> <a class="header-anchor" href="#check-built-in-doc-at-help-plugins-maxima" aria-label="Permalink to “Check built-in Doc at Help -&gt; Plugins -&gt; Maxima”">​</a></h2>',15)])])}const u=i(t,[["render",l]]);export{p as __pageData,u as default};
