import{_ as e,c as l,o as t,aa as r}from"./chunks/framework.BuJSL0aO.js";const p=JSON.parse('{"title":"墨干编辑器V1.0.2","description":"","frontmatter":{},"headers":[],"relativePath":"zh/changelog/v1.0.2.md","filePath":"zh/changelog/v1.0.2.md","lastUpdated":1760514920000}'),i={name:"zh/changelog/v1.0.2.md"};function c(o,a,d,n,h,s){return t(),l("div",null,[...a[0]||(a[0]=[r('<h1 id="墨干编辑器v1-0-2" tabindex="-1">墨干编辑器V1.0.2 <a class="header-anchor" href="#墨干编辑器v1-0-2" aria-label="Permalink to “墨干编辑器V1.0.2”">​</a></h1><h2 id="什么是gnu-texmacs" tabindex="-1">什么是GNU TeXmacs <a class="header-anchor" href="#什么是gnu-texmacs" aria-label="Permalink to “什么是GNU TeXmacs”">​</a></h2><p><a href="https://www.zhihu.com/zvideo/1399501992717332480" target="_blank" rel="">A Quick Tour of GNU TeXmacs</a></p><h2 id="主要变更" tabindex="-1">主要变更 <a class="header-anchor" href="#主要变更" aria-label="Permalink to “主要变更”">​</a></h2><ul><li>全新的图标</li><li>文档入口<code>帮助-&gt;墨干</code>直接加载远程文档</li><li>错误修复 <ul><li>LaTeX的导入导出</li><li>分段函数的排版错乱问题</li><li>修复gnuplot会话在折叠模式下的错误</li></ul></li></ul><h2 id="温馨提示" tabindex="-1">温馨提示 <a class="header-anchor" href="#温馨提示" aria-label="Permalink to “温馨提示”">​</a></h2><h3 id="请新用户们谅解" tabindex="-1">请新用户们谅解 <a class="header-anchor" href="#请新用户们谅解" aria-label="Permalink to “请新用户们谅解”">​</a></h3><p>第一次打开墨干编辑器会比较慢，属于正常现象。目前暂时没有精力做第一次用户的引导界面。</p><h3 id="给老用户们的温馨提示" tabindex="-1">给老用户们的温馨提示 <a class="header-anchor" href="#给老用户们的温馨提示" aria-label="Permalink to “给老用户们的温馨提示”">​</a></h3><p>如果你点击菜单<code>Help-&gt;Mogan</code>或者<code>帮助-&gt;墨干</code>弹出报错对话框，可以按照如下方式清楚历史版本的缓存。</p><p>以Windows平台为例，参考<a href="https://jingyan.baidu.com/article/75ab0bcb08286d97864db2db.html" target="_blank" rel="">如何在Windows中重置GNU TeXmacs?</a> ，注意将用户目录改为<code>%appdata%/Xmacs</code>。macOS和GNU/Linux用户，可以使用命令行<code>rm -rf $HOME/.Xmacs/system/</code>重置墨干编辑器。</p>',11)])])}const _=e(i,[["render",c]]);export{p as __pageData,_ as default};
