import{_ as e,c as o,a2 as r,o as a}from"./chunks/framework.BLTIpkzl.js";const u=JSON.parse('{"title":"墨干理工套件 V1.2.6","description":"","frontmatter":{},"headers":[],"relativePath":"zh/guide/changelog/v1.2.6.md","filePath":"zh/guide/changelog/v1.2.6.md","lastUpdated":1738382165000}'),l={name:"zh/guide/changelog/v1.2.6.md"};function n(i,t,s,g,c,h){return a(),o("div",null,t[0]||(t[0]=[r('<h1 id="墨干理工套件-v1-2-6" tabindex="-1">墨干理工套件 V1.2.6 <a class="header-anchor" href="#墨干理工套件-v1-2-6" aria-label="Permalink to &quot;墨干理工套件 V1.2.6&quot;">​</a></h1><p>墨干理工套件 V1.2.6 包含以下组件：</p><ul><li>墨干 V1.2.6 ( Mogan Research v1.2.6 )</li></ul><h2 id="新功能" tabindex="-1">新功能 <a class="header-anchor" href="#新功能" aria-label="Permalink to &quot;新功能&quot;">​</a></h2><ul><li><strong>安装包</strong>：增加了对 Ubuntu 24.04 的支持</li><li><strong>幻灯片</strong>：支持幻灯片上下移动 by woutersj</li><li><strong>用户界面</strong>：增加了专注模式以切换菜单栏和工具栏的可见性</li><li><strong>字体</strong>：重构字体导入的实现，使用MD5摘要作为导入的字体文件名</li><li><strong>字体</strong>：支持方正小标宋简体（种子用户需求）</li><li><strong>样式</strong>：通过内容摘要进行缓存</li><li><strong>插件</strong>：为插入 UTF-8 编码的 Scheme 代码片段增加了 <code>scheme_u8</code> 协议</li><li><strong>插件</strong>：为《零基础SICP》课程新增了全新的 S7 Scheme 插件</li><li><strong>插件</strong>：重新添加了 Python 插件，且支持 Conda 虚拟环境（种子用户需求）</li><li><strong>插件</strong>：初步支持代码插件</li><li><strong>插件</strong>：初步支持样式插件</li></ul><h2 id="错误修复" tabindex="-1">错误修复 <a class="header-anchor" href="#错误修复" aria-label="Permalink to &quot;错误修复&quot;">​</a></h2><ul><li><strong>稳定性</strong>：修复了 Octave 会话中<code>ones (0)</code>导致的排版失败问题</li><li><strong>编辑器</strong>：修复了<code>编辑-&gt;复制到-&gt;图片</code>功能</li><li><strong>用户界面</strong>：修复了<code>视图-&gt;全屏模式</code>的错误界面样式</li><li><strong>性能</strong>：修复了禁用自动保存后 CPU 使用率达到100%的问题（<a href="https://savannah.gnu.org/bugs/?62156" target="_blank" rel="noreferrer">SAVANNAH #62156</a>）</li><li><strong>布局引擎</strong>：修复了在 Windows 上使用绝对路径链接图像的渲染问题</li><li><strong>OSPP PDF</strong>：修复了一些错误 by mgubi</li></ul><h2 id="开发者" tabindex="-1">开发者 <a class="header-anchor" href="#开发者" aria-label="Permalink to &quot;开发者&quot;">​</a></h2><ul><li>移除对 Qt 5 的支持</li><li>升级到 xmake v2.8.7</li><li>将 <a href="https://github.com/XmacsLabs/moebius" target="_blank" rel="noreferrer">moebius</a> 从墨干理工套件中分离出来</li></ul>',9)]))}const m=e(l,[["render",n]]);export{u as __pageData,m as default};
