import{_ as e,c as r,a3 as n,o as i}from"./chunks/framework.CIdFYEl6.js";const o="/assets/enun_1.Cx-pa0XA.png",a="/assets/enun_2.CUY0TITO.png",s="/assets/enun_3.CnsA18Uu.png",l="/assets/enun_4.DNqVxy30.png",d="/assets/enun_5.D0J_mkxn.png",c="/assets/enun_6.aLSo4YHT.png",g="/assets/enun_7.DOGLtlGW.png",x=JSON.parse('{"title":"Liii STEM的环境插入","description":"","frontmatter":{},"headers":[],"relativePath":"zh/guide/guide-enunciation.md","filePath":"zh/guide/guide-enunciation.md","lastUpdated":1750729574000}'),u={name:"zh/guide/guide-enunciation.md"};function m(h,t,p,_,b,f){return i(),r("div",null,t[0]||(t[0]=[n('<h1 id="liii-stem的环境插入" tabindex="-1">Liii STEM的环境插入 <a class="header-anchor" href="#liii-stem的环境插入" aria-label="Permalink to &quot;Liii STEM的环境插入&quot;">​</a></h1><p>文档来自 <a href="https://liiistem.cn/docs/guide-enunciation.html" target="_blank" rel="noreferrer">Liii STEM 官网</a>。该官网的文档会持续更新，墨干通常会及时同步更新内容，但可能存在一定延迟，请以官方文档为准。</p><h2 id="一、功能介绍" tabindex="-1">一、功能介绍 <a class="header-anchor" href="#一、功能介绍" aria-label="Permalink to &quot;一、功能介绍&quot;">​</a></h2><p>该功能旨在通过插入<strong>语义块（Enunciation）</strong>，为您文章中的<strong>定理（Theorem）</strong>、<strong>引理（Lemma）</strong>、<strong>推论（Corollary）</strong>、<strong>命题（Proposition）</strong> 以及<strong>证明（Proof）</strong> 等部分配置合适的写作<strong>环境</strong>，以保证文章格式排版的美观与规范。本指南将帮助您实现下图展示的最终效果。<br><img src="'+o+'" alt="图1"></p><h2 id="二、操作流程" tabindex="-1">二、操作流程 <a class="header-anchor" href="#二、操作流程" aria-label="Permalink to &quot;二、操作流程&quot;">​</a></h2><p>在工具栏依次点击<code>插入</code> -&gt; <code>语义块</code> -&gt; <code>定理</code><br> （您可以根据需要选择引理、推论、命题、证明等环境）（如下图所示）<br><img src="'+a+'" alt="图2"></p><blockquote><p>注：如果您需要<strong>更多元</strong>的编号或环境格式需求，请您按照以下步骤进一步操作</p><ul><li>将光标置于您添加的语义块上</li><li>在工具栏依次点击<code>焦点</code> -&gt; <code>首选项</code>（如下图所示）</li><li>选择您需要的编号或环境格式，具体格式解释如下表所示<br><img src="'+s+'" alt="图3"></li></ul><table tabindex="0"><thead><tr><th>格式名称</th><th>说明</th></tr></thead><tbody><tr><td>European numbering style</td><td>设置欧洲样式</td></tr><tr><td>Prefix by section number</td><td>添加章节层级</td></tr><tr><td>Framed theorems</td><td>加框</td></tr><tr><td>Hanging theorems</td><td>加悬浮框</td></tr></tbody></table></blockquote><h2 id="三、相关释例" tabindex="-1">三、相关释例 <a class="header-anchor" href="#三、相关释例" aria-label="Permalink to &quot;三、相关释例&quot;">​</a></h2><ol><li><strong>定理（Theorem）</strong>：核心结论，需严格证明，代表文章主要贡献。<br><img src="'+o+'" alt="图1"></li><li><strong>引理（Lemma）</strong>：辅助性结论，为证明定理或命题提供中间步骤。<br><img src="'+l+'" alt="图4"></li><li><strong>推论（Corollary）</strong>：定理或命题的直接延伸结果，常需简短补充证明。 <img src="'+d+'" alt="图5"></li><li><strong>命题（Proposition）</strong>：重要性次于定理的独立结论，可视为小定理或未命名定理。<br><img src="'+c+'" alt="图6"></li><li><strong>证明（Proof）</strong>：逻辑推导过程，验证定理、引理、命题或推论的正确性。<br><img src="'+g+'" alt="图7"></li></ol><h2 id="四、注意事项" tabindex="-1">四、注意事项 <a class="header-anchor" href="#四、注意事项" aria-label="Permalink to &quot;四、注意事项&quot;">​</a></h2><ol><li>在不同语言模式下，同一语义块的呈现效果存在差异。例如，&quot;定理&quot;中的内容在中文模式下呈现正体，而在英文模式下是斜体。</li><li>请您尽量单独使用语义块，避免在一语义块中嵌套另一语义块。</li></ol>',11)]))}const P=e(u,[["render",m]]);export{x as __pageData,P as default};
