import{_ as a}from"./chunks/eq_1.CVoylalp.js";import{_ as t,c as o,a3 as i,o as r}from"./chunks/framework.CIdFYEl6.js";const s="/assets/eq_2.DH6la4PC.png",l="/assets/eq_3.dC3ft9_2.png",d="/assets/eq_4.CYZJ4PqD.png",c="/assets/eq_5.DLdkado0.png",f=JSON.parse('{"title":"Liii STEM的公式编号与引用","description":"","frontmatter":{},"headers":[],"relativePath":"zh/guide/guide-eq.md","filePath":"zh/guide/guide-eq.md","lastUpdated":1750729574000}'),n={name:"zh/guide/guide-eq.md"};function p(h,e,m,u,g,q){return r(),o("div",null,e[0]||(e[0]=[i('<h1 id="liii-stem的公式编号与引用" tabindex="-1">Liii STEM的公式编号与引用 <a class="header-anchor" href="#liii-stem的公式编号与引用" aria-label="Permalink to &quot;Liii STEM的公式编号与引用&quot;">​</a></h1><p>文档来自 <a href="https://liiistem.cn/docs/guide-eq.html" target="_blank" rel="noreferrer">Liii STEM 官网</a>。该官网的文档会持续更新，墨干通常会及时同步更新内容，但可能存在一定延迟，请以官方文档为准。</p><h2 id="一、功能介绍" tabindex="-1">一、功能介绍 <a class="header-anchor" href="#一、功能介绍" aria-label="Permalink to &quot;一、功能介绍&quot;">​</a></h2><p>该功能旨在实现您文章中数学公式的<strong>自动编号</strong>，双向链接以保证序号的<strong>自动更新</strong>，以及在下文中规范而便捷地<strong>引用</strong>您的公式。本指南将帮助您实现下图展示的效果。 <img src="'+a+'" alt="图1"></p><h2 id="二、操作流程" tabindex="-1">二、操作流程 <a class="header-anchor" href="#二、操作流程" aria-label="Permalink to &quot;二、操作流程&quot;">​</a></h2><h4 id="step-1-自动编号" tabindex="-1"><em>STEP 1.</em> 自动编号 <a class="header-anchor" href="#step-1-自动编号" aria-label="Permalink to &quot;*STEP 1.* 自动编号&quot;">​</a></h4><p>将光标置于公式上，点击工具栏上的<code>Ⅳ</code>式样按键（如下图所示）</p><p><img src="'+s+'" alt="图2"></p><h4 id="step-2-公式命名" tabindex="-1"><em>STEP 2.</em> 公式命名 <a class="header-anchor" href="#step-2-公式命名" aria-label="Permalink to &quot;*STEP 2.* 公式命名&quot;">​</a></h4><ul><li>将光标置于公式末尾，确保输入法为英文模式</li><li>点击<code>\\</code>，输入<code>label</code>，（或使用快捷键：<code>ctrl</code>＋<code>!</code>），点击<code>回车</code></li><li>输入您公式的名称，如“eq:linear-model”，点击<code>回车</code>（如下图所示）<br><img src="'+l+'" alt="图3"></li></ul><h4 id="step-3-建立双向链接" tabindex="-1"><em>STEP 3.</em> 建立双向链接 <a class="header-anchor" href="#step-3-建立双向链接" aria-label="Permalink to &quot;*STEP 3.* 建立双向链接&quot;">​</a></h4><ul><li>在您需要引用该公式的地方</li><li>点击<code>\\</code>，输入<code>eqref</code>，点击<code>回车</code></li><li>再次输入您刚才命名的公式名称，点击<code>回车</code>（如下图所示）<br><img src="'+d+'" alt="图4"></li></ul><h4 id="step-4-插入non-breaking-space" tabindex="-1"><em>STEP 4.</em> 插入Non-breaking Space <a class="header-anchor" href="#step-4-插入non-breaking-space" aria-label="Permalink to &quot;*STEP 4.* 插入Non-breaking Space&quot;">​</a></h4><ul><li>将光标置于您想插入的位置</li><li>按住<code>空格</code>，点击<code>tab</code>（如下图所示）<br><img src="'+c+'" alt="图5"></li></ul><blockquote><p>注：Non-breaking Space，即“不间断空格”，可以确保前后两部分位于同一行，不会因为换行而断开。</p></blockquote>',15)]))}const P=t(n,[["render",p]]);export{f as __pageData,P as default};
