import{_ as a,c as t,a3 as o,o as i}from"./chunks/framework.CIdFYEl6.js";const s="/assets/tab_1.Dv17hGs2.png",l="/assets/tab_2.CNXE36CU.png",r="/assets/tab_3.B9KKgCuZ.png",c="/assets/tab_4.CluBgFsg.png",d="/assets/tab_5.C5l328aK.png",n="/assets/tab_6.D6KRJDq-.png",u="/assets/tab_7.DmgwRy1Y.png",f=JSON.parse('{"title":"Liii STEM的表格编号与引用","description":"","frontmatter":{},"headers":[],"relativePath":"zh/guide/guide-table.md","filePath":"zh/guide/guide-table.md","lastUpdated":1750729574000}'),p={name:"zh/guide/guide-table.md"};function b(h,e,m,g,_,q){return i(),t("div",null,e[0]||(e[0]=[o('<h1 id="liii-stem的表格编号与引用" tabindex="-1">Liii STEM的表格编号与引用 <a class="header-anchor" href="#liii-stem的表格编号与引用" aria-label="Permalink to &quot;Liii STEM的表格编号与引用&quot;">​</a></h1><p>文档来自 <a href="https://liiistem.cn/docs/guide-table.html" target="_blank" rel="noreferrer">Liii STEM 官网</a>。该官网的文档会持续更新，墨干通常会及时同步更新内容，但可能存在一定延迟，请以官方文档为准。</p><h2 id="一、功能介绍" tabindex="-1">一、功能介绍 <a class="header-anchor" href="#一、功能介绍" aria-label="Permalink to &quot;一、功能介绍&quot;">​</a></h2><p>该功能旨在实现您文章中表格的<strong>自动编号</strong>，双向链接以保证序号的<strong>自动更新</strong>，以及在下文中规范而便捷地<strong>引用</strong>您的表格。本指南将帮助您实现下图展示的效果。<br><img src="'+s+'" alt="图1"></p><h2 id="二、操作流程" tabindex="-1">二、操作流程 <a class="header-anchor" href="#二、操作流程" aria-label="Permalink to &quot;二、操作流程&quot;">​</a></h2><h4 id="step-1-插入表格" tabindex="-1"><em>STEP 1.</em> 插入表格 <a class="header-anchor" href="#step-1-插入表格" aria-label="Permalink to &quot;*STEP 1.* 插入表格&quot;">​</a></h4><ul><li>在工具栏依次点击<code>插入</code> -&gt; <code>表格</code> -&gt; <code>小表格</code>（如下图所示）<br><img src="'+l+'" alt="图2"></li><li>点击工具栏上带有&quot;+&quot;或&quot;×&quot;的上下左右<code>箭头</code>，以添加或删除行和列（如下图所示） <img src="'+r+'" alt="图3"></li></ul><blockquote><p>注：一般情况下，在您插入表格后系统会自动编号。如发现未编号，您可将光标置于表格上，点击工具栏上的<code>Ⅳ</code>式样按键（如下图所示）自行实现。<br><img src="'+c+'" alt="图4"></p></blockquote><h4 id="step-2-表格命名" tabindex="-1"><em>STEP 2.</em> 表格命名 <a class="header-anchor" href="#step-2-表格命名" aria-label="Permalink to &quot;*STEP 2.* 表格命名&quot;">​</a></h4><ul><li>将光标置于表格说明的末尾，确保输入法为英文模式</li><li>点击<code>\\</code>，输入<code>label</code>，（或使用快捷键：<code>ctrl</code>＋<code>!</code>），点击<code>回车</code></li><li>输入您表格的名称，如&quot;Table1&quot;，点击<code>回车</code>（如下图所示）<br><img src="'+d+'" alt="图5"></li></ul><h4 id="step-3-建立双向链接" tabindex="-1"><em>STEP 3.</em> 建立双向链接 <a class="header-anchor" href="#step-3-建立双向链接" aria-label="Permalink to &quot;*STEP 3.* 建立双向链接&quot;">​</a></h4><ul><li>在您需要引用该表格的地方</li><li>点击<code>\\</code>，输入<code>ref</code>，点击<code>回车</code></li><li>再次输入您刚才命名的表格名称，点击<code>回车</code>（如下图所示）<br><img src="'+n+'" alt="图6"></li></ul><h4 id="step-4-插入non-breaking-space" tabindex="-1"><em>STEP 4.</em> 插入Non-breaking Space <a class="header-anchor" href="#step-4-插入non-breaking-space" aria-label="Permalink to &quot;*STEP 4.* 插入Non-breaking Space&quot;">​</a></h4><ul><li>将光标置于您想插入的位置</li><li>按住<code>空格</code>，点击<code>tab</code>（如下图所示）<br><img src="'+u+'" alt="图7"></li></ul><blockquote><p>注：Non-breaking Space，即&quot;不间断空格&quot;，可以确保前后两部分位于同一行，不会因为换行而断开。</p></blockquote>',15)]))}const P=a(p,[["render",b]]);export{f as __pageData,P as default};
