import{_ as a,a as h}from"./chunks/goldfish_side_effect.CIH6QF-u.js";import{_ as o,c as t,a3 as c,o as i}from"./chunks/framework.CIdFYEl6.js";const g=JSON.parse('{"title":"金鱼 Scheme","description":"","frontmatter":{},"headers":[],"relativePath":"zh/guide/plugin_goldfish.md","filePath":"zh/guide/plugin_goldfish.md","lastUpdated":1750729574000}'),r={name:"zh/guide/plugin_goldfish.md"};function d(l,e,s,m,n,p){return i(),t("div",null,e[0]||(e[0]=[c('<h1 id="金鱼-scheme" tabindex="-1">金鱼 Scheme <a class="header-anchor" href="#金鱼-scheme" aria-label="Permalink to &quot;金鱼 Scheme&quot;">​</a></h1><p><a href="https://gitee.com/LiiiLabs/goldfish" target="_blank" rel="noreferrer">金鱼 Scheme</a>是三鲤 Scheme 的社区版，墨干理工套件是三鲤理工套件的社区版。</p><p>金鱼 Scheme 作为插件内置在墨干理工套件中，Goldfish Scheme 本身不提供 REPL 的实现，墨干理工套件中提供了 Goldfish Scheme 的结构化 REPL。</p><p>Scheme 语言的初学者，可以在墨干中利用 Goldfish Scheme 学习符合 R7RS 标准的 Scheme 语言。</p><h2 id="结构化-repl" tabindex="-1">结构化 REPL <a class="header-anchor" href="#结构化-repl" aria-label="Permalink to &quot;结构化 REPL&quot;">​</a></h2><p>结构化 REPL 和常见的 REPL 的显著区别是输入和输出都可以是结构化的。对于编程语言的 REPL，一般来说，输入都是纯文本格式的代码；在 Racket 中，支持在代码中将图片作为代码的一部分。在金鱼 Scheme 会话中，输入仍旧是纯文本的 Scheme 代码，输出则是结构化文档。本文档以如下示例展示金鱼 Scheme 会话的结构化输出效果：</p><h3 id="特殊的渲染规则" tabindex="-1">特殊的渲染规则 <a class="header-anchor" href="#特殊的渲染规则" aria-label="Permalink to &quot;特殊的渲染规则&quot;">​</a></h3><p>墨干的文档本质上是 Scheme 代码，金鱼 Scheme 会话的求值结果，如果以<code>document</code>、<code>math</code>、<code>equation*</code>、<code>align</code>、<code>with</code>、<code>graphics</code>开头，那么会被渲染为墨干文档片段： <img src="'+a+'" alt=""></p><h3 id="区分副作用和求值结果" tabindex="-1">区分副作用和求值结果 <a class="header-anchor" href="#区分副作用和求值结果" aria-label="Permalink to &quot;区分副作用和求值结果&quot;">​</a></h3><p>求值结果以浅绿色的背景色标注。副作用的背景色是文档的背景色，通常是白色。 <img src="'+h+'" alt=""></p><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><p>无须安装，Goldfish Scheme 本身和 Goldfish Scheme 插件都是在墨干中内置的。</p><h2 id="内置文档见帮助-插件-goldfish-scheme" tabindex="-1">内置文档见<code>帮助-&gt;插件-&gt;Goldfish Scheme</code> <a class="header-anchor" href="#内置文档见帮助-插件-goldfish-scheme" aria-label="Permalink to &quot;内置文档见`帮助-&gt;插件-&gt;Goldfish Scheme`&quot;">​</a></h2>',13)]))}const S=o(r,[["render",d]]);export{g as __pageData,S as default};
