import{_ as e,c as i,a3 as t,o as s}from"./chunks/framework.CIdFYEl6.js";const c=JSON.parse('{"title":"Maxima 会话插件","description":"","frontmatter":{},"headers":[],"relativePath":"zh/guide/plugin_maxima.md","filePath":"zh/guide/plugin_maxima.md","lastUpdated":1748334019000}'),n={name:"zh/guide/plugin_maxima.md"};function l(o,a,r,d,h,m){return s(),i("div",null,a[0]||(a[0]=[t('<h1 id="maxima-会话插件" tabindex="-1">Maxima 会话插件 <a class="header-anchor" href="#maxima-会话插件" aria-label="Permalink to &quot;Maxima 会话插件&quot;">​</a></h1><p>相关教学视频：<a href="https://www.bilibili.com/video/BV1JJ4m1V7Mq/" target="_blank" rel="noreferrer">零基础墨干 09：用 Maxima 插件学微积分</a></p><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><p>Maxima 会话插件依赖于<a href="./plugin_binary_gs.html">Ghostscript 二进制插件</a>，请先按照文档安装 Ghostscript，然后查看<code>帮助-&gt;插件-&gt;Maxima</code>以确保文档中的绘图可以正确渲染。</p><h3 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h3><p>从<a href="https://maxima.sourceforge.io" target="_blank" rel="noreferrer">Maxima 官网</a>下载并安装。</p><p>推荐的 Maxima 版本是 Maxima 5.47.0。</p><h3 id="macos" tabindex="-1">macOS <a class="header-anchor" href="#macos" aria-label="Permalink to &quot;macOS&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>brew install maxima</span></span></code></pre></div><h3 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h3><p>在 Debian 及其衍生中，使用如下命令安装：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo apt install maxima</span></span></code></pre></div><h2 id="小贴士" tabindex="-1">小贴士 <a class="header-anchor" href="#小贴士" aria-label="Permalink to &quot;小贴士&quot;">​</a></h2><ul><li>Ubuntu: 使用 snap 安装的 Maxima 无法在墨干中正常使用 <ul><li>这是由 snap 的机制决定的，故而不建议使用 snap 安装 Maxima</li></ul></li></ul><h2 id="内置文档见帮助-插件-maxima" tabindex="-1">内置文档见<code>帮助-&gt;插件-&gt;Maxima</code> <a class="header-anchor" href="#内置文档见帮助-插件-maxima" aria-label="Permalink to &quot;内置文档见`帮助-&gt;插件-&gt;Maxima`&quot;">​</a></h2>',15)]))}const u=e(n,[["render",l]]);export{c as __pageData,u as default};
