import{_ as i,c as e,o as s,aa as t}from"./chunks/framework.BuJSL0aO.js";const p=JSON.parse('{"title":"Maxima 会话插件","description":"","frontmatter":{},"headers":[],"relativePath":"zh/plugin_maxima.md","filePath":"zh/plugin_maxima.md","lastUpdated":1760514920000}'),l={name:"zh/plugin_maxima.md"};function n(r,a,h,d,o,m){return s(),e("div",null,[...a[0]||(a[0]=[t('<h1 id="maxima-会话插件" tabindex="-1">Maxima 会话插件 <a class="header-anchor" href="#maxima-会话插件" aria-label="Permalink to “Maxima 会话插件”">​</a></h1><p>相关教学视频：<a href="https://www.bilibili.com/video/BV1JJ4m1V7Mq/" target="_blank" rel="">零基础墨干 09：用 Maxima 插件学微积分</a></p><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to “安装”">​</a></h2><p>Maxima 会话插件依赖于<a href="./plugin_binary_gs.html">Ghostscript 二进制插件</a>，请先按照文档安装 Ghostscript，然后查看<code>帮助-&gt;插件-&gt;Maxima</code>以确保文档中的绘图可以正确渲染。</p><h3 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to “Windows”">​</a></h3><p>从<a href="https://maxima.sourceforge.io" target="_blank" rel="">Maxima 官网</a>下载并安装。</p><p>推荐的 Maxima 版本是 Maxima 5.47.0。</p><h3 id="macos" tabindex="-1">macOS <a class="header-anchor" href="#macos" aria-label="Permalink to “macOS”">​</a></h3><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>brew install maxima</span></span></code></pre></div><h3 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to “Linux”">​</a></h3><p>在 Debian 及其衍生中，使用如下命令安装：</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>sudo apt install maxima</span></span></code></pre></div><h2 id="小贴士" tabindex="-1">小贴士 <a class="header-anchor" href="#小贴士" aria-label="Permalink to “小贴士”">​</a></h2><ul><li>Ubuntu: 使用 snap 安装的 Maxima 无法在墨干中正常使用 <ul><li>这是由 snap 的机制决定的，故而不建议使用 snap 安装 Maxima</li></ul></li></ul><h2 id="内置文档见帮助-插件-maxima" tabindex="-1">内置文档见<code>帮助-&gt;插件-&gt;Maxima</code> <a class="header-anchor" href="#内置文档见帮助-插件-maxima" aria-label="Permalink to “内置文档见帮助-&gt;插件-&gt;Maxima”">​</a></h2>',15)])])}const x=i(l,[["render",n]]);export{p as __pageData,x as default};
